import { createAction, createReducer } from "@reduxjs/toolkit";

export const addGood = createAction('counter/addGood');

export const counterReducer = createReducer({value: localStorage.getItem("cart") === null ? 0 : localStorage.getItem("cart")}, (builder) => {
    builder
        .addCase(addGood, (state, action) => {
            state.value += action.payload;
            localStorage.setItem('cart', state.value)
        })
})

export const selectCount = (state) => state.counter.value;
