import React, { useState } from "react";
import GamePreview from "../components/GamePreview";
import "../styles/Shop.css";

const Shop = function(){
    const [games, setgames] = useState([
        {id: 1, title: 'TowerDefence', description: 'A little bit about the good', img: "/images/1.jpg"},
        {id: 2, title: 'Infernal Crusade', description: 'A little bit about the good', img: '/images/1.jpg'},
        {id: 3, title: 'Tail of Marx', description: 'A little bit about the good', img: '/images/1.jpg'},
        {id: 4, title: 'Serious John', description: 'A little bit about the good', img: '/images/1.jpg'},
        {id: 5, title: 'Tail of Marx', description: 'A little bit about the good', img: '/images/1.jpg'},
        {id: 6, title: 'Serious John', description: 'A little bit about the good', img: '/images/1.jpg'},
    ])
    return (
        <div>
            <h1 className="shopTitle">All games</h1>
            <div className="gameList">
                {games.map(game => <GamePreview  game = {game} key = {game.id} />)}
            </div>
        </div>
    );
}

export default Shop;
