import { createAction, createReducer } from "@reduxjs/toolkit";

export const addGood = createAction('counter/addGood');
export const removeGood = createAction('counter/removeGood');

export const counterReducer = createReducer({value: localStorage.getItem("cart") === null ? 0 : JSON.parse(localStorage.getItem("cart"))}, (builder) => {
    builder
        .addCase(addGood, (state, action) => {
            state.value += action.payload;
            localStorage.setItem('cart', JSON.stringify(state.value));
        })
        .addCase(removeGood, (state, action) => {
            state.value -= action.payload;
            localStorage.setItem('cart', JSON.stringify(state.value));
        })
})
