import React, { useState } from "react";
import '../styles/Home.css';
import {
    dicrement,
    increment,
    selectCount,
  } from '../actions/counter.jsx';
import { useDispatch, useSelector } from "react-redux";

const Home = function(){
    const count = useSelector(selectCount);
    const dispatch = useDispatch();
    const [incrementAmount, setIncrementAmount] = useState(2);
    return(
        <header>
            <h1 className="mainTitle">Work at the speed<br />
            of thought</h1>
            <p className="text">Most calendars are designed for teams. Slate is designed for<br /> 
            freelancers who want a simple way to plan their schedule.</p>
            <div className="buttons">
                <button id="butPlay">Try For Free</button>
                <span>{count}</span>
                <button id="butLearn">Learn more</button>
            </div>
            <div>
                <button onClick={() => dispatch(increment(incrementAmount))}>+</button>
                <button onClick={() => dispatch(dicrement(incrementAmount))}>-</button>
            </div>
        </header>
    );
}

export default Home;
