import React, { useState } from "react";
import GamePreview from "../components/GamePreview";
import "../styles/Shop.css";

const Shop = function(){
    const games =([
        {title: 'TowerDefence', description: 'A little bit about the good', img: "/images/1.jpg", price: 25},
        {title: 'Infernal Crusade', description: 'A little bit about the good', img: '/images/1.jpg', price: 80},
        {title: 'Magicka', description: 'A little bit about the good', img: '/images/1.jpg', price: 15},
        {title: 'Serious John', description: 'A little bit about the good', img: '/images/1.jpg', price: 45},
        {title: 'Tail of Marx', description: 'A little bit about the good', img: '/images/1.jpg', price: 50},
        {title: 'Serious John', description: 'A little bit about the good', img: '/images/1.jpg', price: 30},
    ])
    return (
        <div>
            <h1 className="shopTitle text-[64px]">All games</h1>
            <div className="gameList">
                {games.map(game => <GamePreview  game = {game} key = {Date.now()} />)}
            </div>
        </div>
    );
}

export default Shop;
