import { createAction, createReducer } from "@reduxjs/toolkit";

export const addToCard = createAction('card/add');

export const goodsReducer = createReducer({goods: localStorage.getItem("goods") === null ? [] : JSON.parse(localStorage.getItem("goods"))}, builder => {
    builder.addCase(addToCard, (state, action) => {
        state.goods = [...state.goods, action.payload];
    })
})
